class NpmCommands {
  constructor(
    private registry: RegistryClient,
    private resolver: DependencyResolver,
    private fsx: FsxClient,
    private bashx: BashxClient
  )

  async install(args: string[]): Promise<void> {
    if (args.length === 0) {
      return this.installFromPackageJson()
    }
    return this.installPackage(args[0])
  }

  async run(script: string, args: string[]): Promise<RunResult> {
    const pkg = await this.fsx.readJson('package.json')
    const cmd = pkg.scripts?.[script]
    return this.bashx.exec(cmd, { env: this.buildEnv(pkg) })
  }
}

